    }

    return(
        <>
        <div>
            <h1> Hello, </h1>
            <input type = "text" placeholder = "Enter Your Name" onChange = {inpTxt} value={name}/>
            <button>Click Me</button>
        </div>
        </>
    )
}

export default App;
