        // console.log(event.target.value);
        setName(event.target.value);
    }
    const upDate = () =>{
        setUpdatename(name)
    }

    return(
        <>
        <div>
        <form>
            <h1> Hello, {upDatename} </h1>
            <input type = "text" placeholder = "Enter Your Name" onChange={inpTxt} value={name}/>
            <br /> &nbsp; &nbsp;
            <button onClick={upDate}>Click Me âœ‹ </button>
        </form>
        </div>
        </>
    )
}

export default App;
