import React, { useState } from 'react';


const App = () => {

    let newTime = new Date().toLocaleTimeString();
    const newDate = new Date().toLocaleDateString();
    const [ctime, setctime] = useState(newTime); //Hooks in reactjs

    const inTime = () => {
    // console.log("You Clicked me");
    newTime = new Date().toLocaleTimeString();
    setctime(newTime);
    }

    return (
        <>
            <h1>{ctime}</h1>
            {/* <h1>{newDate}</h1> */}
            <button onClick={inTime}>Get Time</button>
        </>
    )
}

export default App;
