import React, { useState } from 'react';


const App = () =>{
    let purpel = "#800080"
    let myName = "Click me"
    const [ bg ,setBg] = useState(purpel);
    const [name ,setName] = useState(myName)
    const bgChange = () =>{
        setBg('#f54e42')
        setName('Aauch 🐱 ')
    }
    const bgBack = () =>{
        setBg(purpel)
        setName('Phir aagya re 😍 🐅 ')
    }

    return (
        <>
        <div style = {{ background : bg }}>
        <button onClick = {bgChange} onDoubleClick = {bgBack}>{name}</button>
        </div>
        </>
    )
}

export default App;
