import React, { useState } from 'react';


const App = () =>{
    
    const [name , setName] = useState();
    const [upDatename ,setUpdatename] = useState();
    const [pass , setPass] = useState();
    const [upDatepass ,setUpdatepass] = useState();
    
    const inpPass = (event) =>{
        setPass(event.target.value)
    }

    const inpTxt = (event) =>{
        // console.log(event.target.value);
        setName(event.target.value);
    }
    const upDate = () =>{
        setUpdatename(name)
    }
    const onSubmits = (event) =>{
        event.preventDefault();
        setUpdatename(name);
    }

    return(
        <>
        <div>
        <form onSubmit={onSubmits}>
            <h1>&nbsp; &nbsp; Hello, {upDatename} </h1>
            <input type = "text" placeholder = "Enter Your FirstName" onChange={inpTxt} value={name}/>
            <br />
            <input type = "text" placeholder = "Enter Your LastName" onChange={inpPass} value={pass}/>
            <br /> &nbsp; &nbsp; &nbsp; &nbsp; 
            <button onClick={upDate} type="submit">Click Me âœ‹ </button>
        </form>
        </div>
        </>
    )
}

export default App;
