import React from 'react';

const Slot = (props) => {
    let { x, y, z } = props;

    if (x == y && y == z) {
        return (
            <>
                <div className="slot_inner">
                    <h1> {x} {y} {z} </h1>
                    <p>This is Matching !!</p>
                </div>
            </>
        )
    }else{
        return (
            <>
                <div className="slot_inner">
                    <h1> {x} {y} {z} </h1>
                    <p>This is Not Matching !!</p>
                </div>
            </>
        )
    }
}



const App = () => {
    return (
        <>
            <h1 className="heading"> 🎰 Welcome to Slot Machine Game 🎰 </h1>
            <div className="slot_machine">
                <Slot x='😄' y='😄' z='😄' />
                <Slot x='😄' y='🎅' z='🎅' />
                <Slot x='🍎' y='🍎' z='🚗' />
                <Slot x='🌹' y='🌹' z='🌹' />
                <Slot x='❤️' y='😍' z='🌹' />
            </div>

        </>
    )
}

export default App;
