import React from 'react';
import Slot from './Slot';


const App = () => {
    return (
        <>
            <h1 className="heading"> 🎰 Welcome to <span style = {{background :rgba(0,0,0,0.1)}}>Slot Machine Game </span>🎰 </h1>
            <div className="slot_machine">
                <Slot x='😄' y='😄' z='😄' />
                <Slot x='😄' y='🎅' z='🎅' />
                <Slot x='🍎' y='🍎' z='🚗' />
                <Slot x='🌹' y='🌹' z='🌹' />
                <Slot x='❤️' y='😍' z='🌹' />
            </div>

        </>
    )
}

export default App;
